  var IC, results;

  ({ IC, ...results } = res);

  IC = parseFloat(IC);

  const rows = Object.values(results).map(obj =&gt; {
    return `
    &lt;tr&gt;
      &lt;td&gt;${obj.code} | ${obj.libelle}&lt;/td&gt;
      &lt;td&gt;${obj.probabilite.toFixed(3)}&lt;/td&gt;
